import axios from "axios";
import React, { Component } from "react";

export default class AddReservation extends Component {
    constructor(props){
        super(props);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onChangeTime = this.onChangeTime.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state={
            name:'',
            date:'',
            time: ''
        }
    }

    onChangeName(e){
        this.setState({
            name: e.target.value
        })
    }

    onChangeDate(e){
        this.setState({
            date: e.target.value
        })
    }

    onChangeTime(e){
        this.setState({
            time: e.target.value
        })
    }
    
    onSubmit(e){
       e.preventDefault();
       console.log(this.state.name)
       console.log(this.state.date)
       console.log(this.state.time)

        const reservation = {
            name: this.state.name,
            date: this.state.date,
            time: this.state.time
        }

        axios.post('http://localhost:8081/addReservation', reservation)
            .then(res => {
                console.log(res.data);
                // Handle success or UI changes if needed
            })
            .catch(error => {
                console.error('Error:', error);
                // Handle errors or show error messages
            });

       this.setState({
            name:'',
            date:'',
            time: ''
       })
    }
    
    render() {
        return (
            <div className="container">
                <div className="col-lg-4"></div>
                <div className="col-lg-6">
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                            <label htmlFor="name">Name</label>
                            <input type="text" className="form-control" value={this.state.name} onChange={this.onChangeName} />
                        </div>

                        <div className="form-group">
                            <label htmlFor="date">Date</label>
                            <input type="date" className="form-control" value={this.state.date} onChange={this.onChangeDate} />
                        </div>

                        <div className="form-group">
                            <label htmlFor="time">Time</label>
                            <input type="time" className="form-control" value={this.state.time} onChange={this.onChangeTime} />
                        </div>
                        
                        <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
                </div>
            </div>
        )
    }
}
